package com.javavideo.lesson2;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	boolean pagla=true;
		int logo=23;
	while (pagla) {
		System.out.println("charbo na bara");
		logo++;
		if (logo>100)
			pagla=false;
	}

	}

}
